graphql.query:
mutation($fullname:String!,$email:String!,$password:String!,$password_confirmation:String!) {
  register(
    input: {
      fullname: $fullname
      email: $email
      password: $password
      password_confirmation: $password_confirmation
    }
  ) {
    tokens{
      user{
        id
        fullname
        email
      }
      access_token
    }
    status
  }
}

mutation($email:String!,$password:String!) {
  login(
    input: {
      username: $email
      password: $password
    }
  ) {
    access_token
    refresh_token
    expires_in
    token_type
    user{
        id
        fullname
    }
  }
}

mutation($email: String!) {
  forgotPassword(
    input: {
      email: $email
    }
  ) {
    status
    message
  }
}

query{
