mutation($fullname:String!,
$email:String!,$password:String!,$password_confirmation:String!,
$phone:String) {
  register(
    input: {
      fullname: $fullname
      email: $email
      phone: $phone
      password: $password
      password_confirmation: $password_confirmation
    }
  ) {
    tokens{
      user{
        id
        fullname
        phone
        email
      }
      access_token
    }
    status
  }
}

mutation($email:String!,$password:String!) {
  login(
    input: {
      username: $email
      password: $password
    }
  ) {
    access_token
    refresh_token
    expires_in
    token_type
    user{
        id
        fullname
    }
  }
}

mutation($email: String!) {
  forgotPassword(
    input: {
      email: $email
    }
  ) {
    status
    message
  }
}

mutation(
    $email: String!
    $token: String!
    $password: String!
    $password_confirmation: String!
) {
  updateForgottenPassword(
    input: {
      email: $email
    token:$token
    password: $password
    password_confirmation: $password_confirmation

    }
  ) {
    status
    message
  }
}

mutation(
    $old_password: String!
    $password: String!
    $password_confirmation: String!
) {
  updatePassword(
    input: {
    old_password: $old_password
    password: $password
    password_confirmation: $password_confirmation
    }
  ) {
    status
    message
  }
}

